import { AppBar, IconButton, Toolbar, Typography, Box, Tooltip } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';
import { useThemeControl } from '../../App';
import { useTheme } from '@mui/material/styles';
import logoUrl from '../../assets/images/Logo.png';
type Props = {
  onMenuClick: () => void;
  title?: string;
  isMobile: boolean;
};
export default function TopBar({ onMenuClick, isMobile, title }: Props): React.ReactElement {
  const { toggleTheme } = useThemeControl();
  const theme = useTheme();
  const isDark = theme.palette.mode === 'dark';
  return (
    <AppBar position="fixed" color="primary" enableColorOnDark>
      <Toolbar sx={{ gap: 1 }}>
        {isMobile && (
          <Tooltip title={'Menu'}>
            <IconButton edge="start" color="inherit" onClick={onMenuClick}>
              <MenuIcon />
            </IconButton>
          </Tooltip>
        )}
        <Typography sx={{ variant: 'h6', flexGrow: 1 }} noWrap>
          <img
            src="../../assets/images/Logo.png"
            alt="Logo"
            style={{ height: 40, marginLeft: 8, marginRight: 16 }}
          />
          {title ?? 'PPE-Watcher'}
        </Typography>
        <Tooltip title={isDark ? 'Switch to light' : 'Switch to dark'}>
          <IconButton color="inherit" onClick={toggleTheme} aria-label="toggle theme">
            {isDark ? <LightModeIcon /> : <DarkModeIcon />}
          </IconButton>
        </Tooltip>
      </Toolbar>
    </AppBar>
  );
}
