// pages/Dashboard/Alerts/index.tsx
import * as React from 'react';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
// ✅ 关键：用 Grid v2 的导入方式（根据你的 MUI 版本二选一）
import Grid from '@mui/material/Grid2'; // 如果是 MUI v6
// import Grid from '@mui/material/Unstable_Grid2'; // 如果是 v5 + Unstable_Grid2

const ALERT_STATS = [
  { label: 'Open', value: 1 },
  { label: 'Ack', value: 1 },
  { label: 'Resolved', value: 1 },
  { label: 'Total', value: 3 }, // 别忘了填值
];

export default function Alerts(): React.ReactElement {
  return (
    <Grid
      container
      direction="column"
      spacing={2}
      sx={{ bgcolor: 'background.paper', flex: 1, minHeight: 0 }}
    >
      {/* 标题 */}
      <Grid size={{ xs: 12 }}>
        <Typography variant="h6" sx={{ flex: 1 }}>
          Alerts
        </Typography>
      </Grid>

      {/* 顶部 4 个统计卡片 */}
      <Grid size={{ xs: 12 }}>
        <Grid container spacing={2}>
          {ALERT_STATS.map((i) => (
            <Grid key={i.label} size={{ xs: 12, sm: 6, md: 3 }}>
              <Paper
                elevation={0}
                sx={{
                  bgcolor: 'background.paper',
                  p: 2,
                  border: '1px solid',
                  borderColor: 'divider',
                  borderRadius: 2,
                }}
              >
                <Stack spacing={0.5}>
                  <Typography variant="body2" color="text.secondary">
                    {i.label}
                  </Typography>
                  <Typography variant="h4" color="primary.main" fontWeight={700}>
                    {i.value}
                  </Typography>
                </Stack>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Grid>

      {/* 下面的列表/表格区域（先占位） */}
      <Grid size={{ xs: 12 }} sx={{ flex: 1, minHeight: 0 }}>
        <Paper
          elevation={0}
          sx={{
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            border: '1px solid',
            borderColor: 'divider',
            borderRadius: 2,
          }}
        >
          <Stack direction="row" alignItems="center" justifyContent="space-between" sx={{ p: 2 }}>
            <Typography variant="subtitle1">Latest Alert</Typography>
            {/* 右侧放筛选/按钮都可 */}
          </Stack>
          <Divider />
          <div style={{ flex: 1, overflow: 'auto' }}>
            {/* 表格或列表内容放这里 */}
            <div style={{ padding: 16 }}>Table / list goes here…</div>
          </div>
        </Paper>
      </Grid>
    </Grid>
  );
}
