import { createBrowserRouter, RouteObject } from 'react-router-dom';
import AppShell from 'components/layout/AppShell';

// Main menu pages
import Dashboard from 'pages/Dashboard';
import History from 'pages/History';
import Settings from 'pages/Settings';
import Login from 'pages/Login';
import NotFound from 'pages/NotFound';

// Dashboard sub-pages
import Overview from 'pages/Dashboard/Overview';
import LiveFeed from 'pages/Dashboard/LiveFeed';
import Alerts from 'pages/Dashboard/Alerts';

// History sub-pages
import SearchViolations from 'pages/History/SearchViolations';
import Archive from 'pages/History/Archive';
import Snapshots from 'pages/History/Snapshots';
import Reports from 'pages/History/Reports';

// Settings sub-pages
import AlertsSettings from 'pages/Settings/AlertsSettings';
import Contacts from 'pages/Settings/Contacts';
import SenderConfig from 'pages/Settings/SenderConfig';
import Security from 'pages/Settings/Security';

const routes: RouteObject[] = [
  // Login route
  { path: '/login', element: <Login /> },
  {
    // Root path with AppShell layout (TopBar, SideNav, Footer)
    path: '/',
    element: <AppShell />,
    children: [
      // Default route → Dashboard
      {
        index: true,
        element: <Dashboard />,
      },
      {
        // Dashboard main and its sub-routes
        path: 'dashboard',
        element: <Dashboard />,
        children: [
          { index: true, element: <Overview /> }, // Default dashboard page: Overview
          { path: 'live', element: <LiveFeed /> }, // Real-time camera feed
          { path: 'alerts', element: <Alerts /> }, // Real-time alerts list
        ],
      },
      {
        // History main and its sub-routes
        path: 'history',
        element: <History />,
        children: [
          { index: true, element: <SearchViolations /> }, // Default history page: Search
          { path: 'archive', element: <Archive /> }, // Archived violations
          { path: 'snapshots', element: <Snapshots /> }, // Violation snapshots
          { path: 'reports', element: <Reports /> }, // Monthly/quarterly reports
        ],
      },
      {
        // Settings main and its sub-routes
        path: 'settings',
        element: <Settings />,
        children: [
          { index: true, element: <AlertsSettings /> }, // Default settings page: Alerts
          { path: 'contacts', element: <Contacts /> }, // Manage alert contacts
          { path: 'sender', element: <SenderConfig /> }, // Email/SMS sender configuration
          { path: 'security', element: <Security /> }, // Change password, security settings
        ],
      },
      // Fallback for non-existing routes
      { path: '*', element: <NotFound /> },
    ],
  },
];

const router = createBrowserRouter(routes);
export default router;
