import * as react from 'react';
import {
  Chip,
  Grid,
  Stack,
  Card,
  CardHeader,
  CardContent,
  Typography,
  Divider,
  Button,
  Box,
} from '@mui/material';
import { useTheme } from '@mui/material';
import { mockViolations } from 'mock/violations';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  LineChart,
  Line,
} from 'recharts';
import { format, startOfDay } from 'date-fns';
import { useMemo, useState } from 'react';
type violationData = {
  id: string;
  type: string[];
  status: string;
  handler: null | string;
  timestamp: string;
  imageUrl: string;
};
const allTypes = ['No Mask', 'No Helmet', 'No Vest', 'No Gloves'] as const;
type PPEType = (typeof allTypes)[number];
type bucket = {
  date: string;
  total: number;
  open: number;
  resolved: number;
  byType: Record<PPEType, number>;
};
function groupByDay(violations: violationData[]): bucket[] {
  const map = new Map<string, bucket>();
  for (const v of violations) {
    const d = startOfDay(new Date(v.timestamp));
    const key = format(d, 'yyyy-MM-dd');
    if (!map.has(key)) {
      map.set(key, {
        date: key,
        total: 0,
        open: 0,
        resolved: 0,
        byType: Object.fromEntries(allTypes.map((t) => [t, 0])) as Record<string, number>,
      });
    }
    const bucket = map.get(key)!;
    bucket.total += 1;
    if (v.status === 'open') bucket.open += 1;
    else if (v.status === 'resolved') bucket.resolved += 1;
    v.type.forEach((t) => (bucket.byType[t as PPEType] += 1));
  }
  return Array.from(map.values()).sort((a, b) => a.date.localeCompare(b.date));
}
export default function Trends(): React.ReactElement {
  const theme = useTheme();
  const buckets = useMemo(() => groupByDay(mockViolations), []);
  const [selected, setSelected] = useState<bucket | null>(buckets.at(-1) ?? null);
  const CustomTooltip = ({ active, payload, label }: any) =>
    active && payload?.length ? (
      <Box sx={{ p: 1.5, bgcolor: 'background.paper', borderRadius: 1, boxShadow: 2 }}>
        <Typography variant="subtitle2">{label}</Typography>
        {payload.map((p: any) => (
          <Typography key={p.dataKey} variant="body2">
            {p.name}: {p.value}
          </Typography>
        ))}
      </Box>
    ) : null;
  return (
    <Grid container sx={{ flex: 1, minHeight: 0, bgcolor: 'background.paper' }} direction="column">
      <Grid size={{ xs: 12 }}>
        <Typography variant={'h6'}>Trends</Typography>
      </Grid>
      <Grid container size={{ xs: 12 }}>
        <Grid
          container
          size={{ xs: 12 }}
          sx={{ borderTop: '1px solid', borderBottom: '1px solid' }}
        >
          <Grid size={{ xs: 6, md: 10 }}>ToolBar</Grid>
          <Grid size={{ xs: 6, md: 2 }}>
            <Button>Generate</Button>
            <Button>Export</Button>
          </Grid>
        </Grid>
        <Grid size={{ xs: 12 }}>
          <Chip label={'TOOlBAR'} />
        </Grid>
      </Grid>
      <Grid container size={{ xs: 12 }} sx={{ flex: 1, minHeight: 0 }}>
        <Grid size={{ xs: 12, md: 10 }} sx={{ borderRight: '1px solid' }}>
          <Box sx={{ flex: 1, minWidth: 0 }}>
            <Typography variant="subtitle1" sx={{ mb: 1 }}>
              Chart
            </Typography>

            {/* 柱状图（堆叠：open/resolved） */}
            <Box sx={{ height: 280 }}>
              <ResponsiveContainer width="100%" height="100%">
                <BarChart
                  data={buckets}
                  onClick={(e: any) => {
                    // 点击柱子选中某天（activePayload 取第一项）
                    const payload = e?.activePayload?.[0]?.payload as bucket | undefined;
                    if (payload) setSelected(payload);
                  }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis allowDecimals={false} />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                  {/* 你也可以只画 total */}
                  <Bar dataKey="open" name="Open" stackId="s" fill="#f59e0b" />
                  <Bar dataKey="resolved" name="Resolved" stackId="s" fill="#22c55e" />
                </BarChart>
              </ResponsiveContainer>
              <ResponsiveContainer width="100%" height={360}>
                <LineChart data={data} margin={{ top: 12, right: 16, bottom: 12, left: 0 }}>
                  <CartesianGrid strokeDasharray="3 3" strokeOpacity={0.3} />
                  <XAxis
                    dataKey="date"
                    tick={{ fontSize: 12, fill: theme.palette.text.secondary }}
                    axisLine={{ stroke: theme.palette.divider }}
                    tickLine={{ stroke: theme.palette.divider }}
                  />
                  <YAxis
                    allowDecimals={false}
                    tick={{ fontSize: 12, fill: theme.palette.text.secondary }}
                    axisLine={{ stroke: theme.palette.divider }}
                    tickLine={{ stroke: theme.palette.divider }}
                  />
                  <Tooltip
                    formatter={(value: any) => [value as number, '']}
                    labelFormatter={(label: string) => `Date: ${label}`}
                  />
                  <Legend verticalAlign="top" height={24} />

                  {/* 三条折线：Total / Open / Resolved */}
                  <Line
                    type="monotone"
                    dataKey="total"
                    name="Total"
                    stroke={theme.palette.primary.main}
                    strokeWidth={2}
                    dot={false}
                    activeDot={{ r: 4 }}
                  />
                  <Line
                    type="monotone"
                    dataKey="open"
                    name="Open"
                    stroke={theme.palette.error.main}
                    strokeWidth={2}
                    dot={false}
                    activeDot={{ r: 4 }}
                  />
                  <Line
                    type="monotone"
                    dataKey="resolved"
                    name="Resolved"
                    stroke={theme.palette.success.main}
                    strokeWidth={2}
                    dot={false}
                    activeDot={{ r: 4 }}
                  />
                </LineChart>
              </ResponsiveContainer>
            </Box>
          </Box>
        </Grid>
        <Grid size={{ xs: 12, md: 2 }}>details</Grid>
      </Grid>
    </Grid>
  );
}
