import * as react from 'react';
import {
  Checkbox,
  FormControlLabel,
  FormGroup,
  Grid,
  Stack,
  Card,
  CardHeader,
  CardContent,
  Container,
  Paper,
  Divider,
  Typography,
  Button,
} from '@mui/material';

const PPET: string[] = [
  'Helmet',
  'Mask',
  'Reflective Vest',
  'Safety Gloves',
  'Goggles',
  'Safety Boots',
];

export default function AlertSettings(): React.ReactElement {
  return (
    <Container maxWidth={'md'} sx={{ flex: 1, display: 'flex' }}>
      <Card sx={{ flex: 1 }}>
        <CardHeader title={'Alert&Notifications Settings'} />
        <Divider />
        <CardContent>
          <Grid container sx={{ flex: 1 }} direction="column">
            <Grid size={{ xs: 12 }}>
              <Typography> PPE Type</Typography>
              <FormGroup
                row
                sx={{
                  flexWrap: 'wrap',
                  justifyContent: 'center',
                  '& .MuiFormControlLabel-root': {
                    flex: { xs: '1 1 100%', md: '1 1 45%' },
                    maxWidth: { xs: '100%', md: '45%' },
                    boxSizing: 'border-box',
                    mx: 1,
                  },
                }}
              >
                {PPET.map((item) => (
                  <FormControlLabel key={item} label={item} control={<Checkbox />} />
                ))}
              </FormGroup>
            </Grid>
            <Grid size={{ xs: 12 }} sx={{ my: 3 }}>
              <Typography> Notifications Type</Typography>
              <FormGroup
                row
                sx={{
                  flexWrap: 'wrap',
                  justifyContent: 'center',
                  '& .MuiFormControlLabel-root': {
                    flex: { xs: '1 1 100%', md: '1 1 45%' },
                    maxWidth: { xs: '100%', md: '45%' },
                    boxSizing: 'border-box',
                    mx: 1,
                  },
                }}
              >
                {['EMAIL', 'SMS'].map((item) => (
                  <FormControlLabel key={item} label={item} control={<Checkbox />} />
                ))}
              </FormGroup>
            </Grid>
            <Grid size={{ xs: 12 }}>
              <Grid container justifyContent={'center'} sx={{ mt: 4 }}>
                <Button variant={'contained'}>SAVE</Button>
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </Container>
  );
}
