import * as react from 'react';
import {
  Drawer,
  List,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Toolbar,
  Box,
} from '@mui/material';
import { NavLink, useLocation } from 'react-router-dom';
const drawerWidth = 240;
type props = {
  isMobile: boolean;
};

function deriveMainKey(pathname: string): 'dashboard' | 'history' | 'settings' {
  if (pathname.startsWith('/settings')) return 'settings';
  if (pathname.startsWith('/history')) return 'history';
  return 'dashboard';
}
export default function SideNav({ isMobile }: props): React.ReactElement {
  const { pathname } = useLocation();
  const MainKey = deriveMainKey(pathname);

  const items =
    MainKey === 'dashboard'
      ? [{ to: '/', label: 'Overview' }]
      : MainKey === 'history'
        ? [{ to: '/history', label: 'All Events' }]
        : [
            { to: '/settings/general', label: 'General' },
            { to: '/settings/account', label: 'Account' },
          ];
  return (
    <Drawer
      variant="permanent"
      open
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
        },
      }}
    >
      <Toolbar />
      <Toolbar />
      <Box role="navigation">
        <List></List>
      </Box>
    </Drawer>
  );
}

export { drawerWidth };
