import React from "react";

export default function WebRtcLoopback() {
  const localVideo = React.useRef<HTMLVideoElement>(null);
  const remoteVideo = React.useRef<HTMLVideoElement>(null);
  const hiddenFileVideo = React.useRef<HTMLVideoElement>(null);

  const pc1 = React.useRef<RTCPeerConnection | null>(null); // 发送端
  const pc2 = React.useRef<RTCPeerConnection | null>(null); // 接收端
  const sourceStream = React.useRef<MediaStream | null>(null);

  async function start(useWebcam = true) {
    await stop(); // 先清理

    // 1) 准备源流：二选一
    if (useWebcam) {
      sourceStream.current = await navigator.mediaDevices.getUserMedia({
        video: { width: { ideal: 1280 }, height: { ideal: 720 } },
        audio: false,
      });
    } else {
      // 用文件伪装实时流，更稳定的本地演示
      const hv = hiddenFileVideo.current!;
      hv.src = "/sample.mp4"; // 放到 public/sample.mp4
      hv.muted = true;
      hv.loop = true;
      await hv.play();
      const cap =
        (hv as any).captureStream?.() ?? (hv as any).mozCaptureStream?.();
      if (!cap) throw new Error("captureStream() not supported");
      sourceStream.current = cap;
    }

    if (localVideo.current) {
      localVideo.current.srcObject = sourceStream.current!; // 本地预览（可选）
      await localVideo.current.play().catch(() => {});
    }

    // 2) 建立两个 PeerConnection（模拟本地与远端）
    pc1.current = new RTCPeerConnection({ iceServers: [{ urls: "stun:stun.l.google.com:19302" }] });
    pc2.current = new RTCPeerConnection({ iceServers: [{ urls: "stun:stun.l.google.com:19302" }] });

    // 3) 互换 ICE 候选
    pc1.current.onicecandidate = e => e.candidate && pc2.current!.addIceCandidate(e.candidate);
    pc2.current.onicecandidate = e => e.candidate && pc1.current!.addIceCandidate(e.candidate);

    // 4) 远端收到媒体后渲染
    pc2.current.ontrack = e => {
      if (remoteVideo.current) {
        remoteVideo.current.srcObject = e.streams[0];
        remoteVideo.current.play().catch(() => {});
      }
    };

    // 5) 发送端把轨道加入连接
    sourceStream.current.getTracks().forEach(t => pc1.current!.addTrack(t, sourceStream.current!));

    // 6) Offer/Answer 流程（真实场景这一步通过信令服务器传递）
    const offer = await pc1.current.createOffer();
    await pc1.current.setLocalDescription(offer);
    await pc2.current.setRemoteDescription(offer);

    const answer = await pc2.current.createAnswer();
    await pc2.current.setLocalDescription(answer);
    await pc1.current.setRemoteDescription(answer);
  }

  async function stop() {
    pc1.current?.getSenders().forEach(s => s.track?.stop());
    pc2.current?.getSenders().forEach(s => s.track?.stop());
    pc1.current?.close(); pc2.current?.close();
    pc1.current = null; pc2.current = null;

    sourceStream.current?.getTracks().forEach(t => t.stop());
    sourceStream.current = null;

    [localVideo.current, remoteVideo.current, hiddenFileVideo.current].forEach(v => {
      if (!v) return;
      v.pause();
      (v as any).srcObject = null;
      v.removeAttribute("src");
      v.load();
    });
  }

  React.useEffect(() => () => { stop(); }, []); // 卸载清理

  return (
    <div style={{ display: "grid", gap: 8 }}>
      {/* 隐藏文件视频源（仅 file 模式需要） */}
      <video ref={hiddenFileVideo} style={{ display: "none" }} playsInline />

      <div style={{ display: "flex", gap: 8 }}>
        <button onClick={() => start(true)}>Start (Webcam)</button>
        <button onClick={() => start(false)}>Start (File loop)</button>
        <button onClick={stop}>Stop</button>
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 8 }}>
        <div>
          <div>Local</div>
          <video ref={localVideo} autoPlay playsInline muted style={{ width: "100%", background: "#000" }} />
        </div>
        <div>
          <div>Remote (from RTCPeerConnection)</div>
          <video ref={remoteVideo} autoPlay playsInline muted style={{ width: "100%", background: "#000" }} />
        </div>
      </div>
    </div>
  );
}
