import * as react from 'react';
import { AppBar, Toolbar, Tabs, Tab, useTheme, useMediaQuery } from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import { drawerWidth } from './SideNav';

// Define main menu groups with their default landing path
const Main = [
  { key: 'dashboard', label: 'Dashboard', defaultPath: '/overview' },
  { key: 'history', label: 'History', defaultPath: '/violations' },
  { key: 'settings', label: 'Settings', defaultPath: '/alerts-settings' },
] as const;

type MainKey = (typeof Main)[number]['key'];

// Define which URL segments belong to each main menu group
// Example: /overview → "dashboard", /violations → "history"
const GROUPS: Record<MainKey, Set<string>> = {
  dashboard: new Set(['overview', 'live', 'alerts']),
  history: new Set(['violations', 'archive', 'snapshots', 'reports']),
  settings: new Set(['alerts-settings', 'contacts', 'sender', 'security']),
};

// Utility: derive main menu key based on current URL path
function deriveMainKey(pathname: string): MainKey {
  const first = pathname.split('/')[1] ?? ''; // extract the first path segment

  if (GROUPS.history.has(first)) return 'history'; // belongs to History group
  if (GROUPS.settings.has(first)) return 'settings'; // belongs to Settings group

  return 'dashboard'; // fallback → default to Dashboard group
}
type Props = { isMobile: boolean };

export default function MainNav({ isMobile }: Props): React.ReactElement | null {
  const navigate = useNavigate();
  const { pathname } = useLocation();
  const mainKey = deriveMainKey(pathname);
  const idx = Math.max(
    0,
    Main.findIndex((m) => m.key === mainKey)
  );
  const handleChange = (_event: React.SyntheticEvent, i: number) => {
    navigate(Main[i].defaultPath);
  };
  return (
    <AppBar
      position="fixed"
      color="default"
      elevation={0}
      sx={{
        top: { xs: 56, sm: 64 },
        zIndex: (t) => t.zIndex.appBar - 1,
      }}
    >
      {' '}
      <Toolbar sx={{ p: 0 }}>
        <Tabs
          value={idx}
          onChange={handleChange}
          sx={{ ml: 1, pl: { md: `${drawerWidth - 40}px` } }}
        >
          {Main.map((m) => (
            <Tab key={m.key} label={m.label} sx={{ mx: 5 }} />
          ))}
        </Tabs>
      </Toolbar>
    </AppBar>
  );
}
