import * as react from 'react';
import { AppBar, Toolbar, Tabs, Tab, useTheme, useMediaQuery } from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import { drawerWidth } from './SideNav';
import { useMemo } from 'react';

const Main = [
  { key: 'dashboard', label: 'Dashboard', defaultPath: '/overview' },
  { key: 'history', label: 'History', defaultPath: '/violations' },
  { key: 'settings', label: 'Settings', defaultPath: '/alerts-settings' },
] as const;

type MainKey = (typeof Main)[number]['key'];

// key 对应的默认跳转路径
const keyToDefault: Record<MainKey, string> = {
  dashboard: '/overview',
  history: '/violations',
  settings: '/alerts-settings',
};

// 根据 URL 推导主菜单
function deriveMainKey(pathname: string): MainKey {
  const first = pathname.split('/')[1] ?? '';
  if (['violations', 'archive', 'snapshots', 'reports'].includes(first)) return 'history';
  if (['alerts-settings', 'contacts', 'sender', 'security'].includes(first)) return 'settings';
  return 'dashboard';
}

// Utility: derive main menu key based on current URL path

type Props = { isMobile: boolean };

export default function MainNav({ isMobile }: Props): React.ReactElement | null {
  const navigate = useNavigate();
  const { pathname } = useLocation();
  const mainKey = deriveMainKey(pathname);
  const activeKey = useMemo<MainKey>(() => deriveMainKey(pathname), [pathname]);

  // 点击 Tab 跳转
  const handleChange = (_: React.SyntheticEvent, nextKey: MainKey) => {
    navigate(keyToDefault[nextKey]);
  };

  return (
    <AppBar
      position="fixed"
      color="default"
      elevation={0}
      sx={{
        top: { xs: 56, sm: 64 },
        zIndex: (t) => t.zIndex.appBar - 1,
      }}
    >
      {' '}
      <Toolbar sx={{ p: 0 }}>
        <Tabs
          value={activeKey}
          onChange={handleChange}
          sx={{ ml: 1, pl: { md: `${drawerWidth - 40}px` } }}
        >
          {Main.map((m) => (
            <Tab key={m.key} label={m.label} sx={{ mx: 5 }} />
          ))}
        </Tabs>
      </Toolbar>
    </AppBar>
  );
}
