import * as React from 'react';
import Grid from '@mui/material/Grid'; // v2：直接导入 Grid
import {
  Box,
  Paper,
  Table,
  TableBody,
  TableRow,
  TableCell,
  TableContainer,
  Typography,
} from '@mui/material';

const ALERT_STATS = [
  { label: 'Open', value: 1 },
  { label: 'Acknowledge', value: 1 },
  { label: 'Resolved', value: 1 },
  { label: 'Total', value: 3 },
];

export default function Alerts(): React.ReactElement {
  return (
    // 顶部两块自然高度，底部主区占满剩余高度
    <Grid container direction="column" spacing={2} sx={{ flex: 1, minHeight: 0 }}>
      {/* 标题 */}
      <Grid size={{ xs: 12 }}>
        <Typography variant="h6">Alerts</Typography>
      </Grid>

      {/* 统计卡片 */}
      <Grid size={{ xs: 12 }}>
        <Grid container spacing={2}>
          {ALERT_STATS.map((i) => (
            <Grid key={i.label} size={{ xs: 12, sm: 4, md: 3 }}>
              <Paper sx={{ p: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  {i.label}
                </Typography>
                <Typography variant="h5" fontWeight={700}>
                  {i.value}
                </Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Grid>

      {/* 底部主区：左右分栏，占满剩余高度 */}
      <Grid size={{ xs: 12 }} sx={{ flex: 1, minHeight: 0 }}>
        <Grid container spacing={1} sx={{ height: '100%', minHeight: 0 }}>
          {/* 左侧列表 3/12 宽 */}
          <Grid size={{ xs: 12, md: 3 }}>
            <Paper sx={{ p: 2, height: '100%' }}>List</Paper>
          </Grid>

          {/* 右侧 9/12 宽，内部用 CSS Grid 做 3:1 垂直切分 */}
          <Grid size={{ xs: 12, md: 9 }} sx={{ minHeight: 0 }}>
            <Box
              sx={{
                display: 'grid',
                gridTemplateRows: '3fr 1fr', // 上下 3:1
                gap: 1,
                height: '100%',
                minHeight: 0,
              }}
            >
              <Paper sx={{ p: 1 }}>ScreenShot</Paper>

              {/* 表格滚动放在容器上 */}
              <TableContainer component={Paper} sx={{ minHeight: 0, overflow: 'auto' }}>
                <Table size="small">
                  <TableBody>
                    <TableRow>
                      <TableCell>Row 1</TableCell>
                      <TableCell>…</TableCell>
                    </TableRow>
                    <TableRow>
                      <TableCell>Row 2</TableCell>
                      <TableCell>…</TableCell>
                    </TableRow>
                    {/* 继续渲染你的 rows */}
                  </TableBody>
                </Table>
              </TableContainer>
            </Box>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
}
