import { Grid, Box, Typography, Table, TableBody, TableContainer, Paper } from '@mui/material';

export default function Alerts() {
  return (
    // 父级要能占满可用高度
    <Grid container spacing={2} sx={{ height: '100%', minHeight: 0 }}>
      <Grid xs={12}>
        <Typography variant="h6">Alerts</Typography>
      </Grid>

      <Grid xs={12} sm={4} md={3}>
        List
      </Grid>

      {/* 右侧：用 CSS Grid 做 3:1 切分，更稳定 */}
      <Grid xs={12} sm={8} md={9} sx={{ height: '100%', minHeight: 0 }}>
        <Box
          sx={{
            display: 'grid',
            gridTemplateRows: '3fr 1fr', // ← 3:1
            gap: 1,
            height: '100%',
            minHeight: 0,
          }}
        >
          <Box sx={{ border: '1px solid' }}>ScreenShot</Box>

          {/* 用 TableContainer 控制高度与滚动 */}
          <TableContainer
            component={Paper}
            sx={{ height: '100%', minHeight: 0, overflow: 'auto', border: '1px solid' }}
          >
            <Table size="small">
              <TableBody>{/* ...rows */}</TableBody>
            </Table>
          </TableContainer>
        </Box>
      </Grid>
    </Grid>
  );
}
