import * as React from 'react';
import Grid from '@mui/material';
import { Box, Paper, Table, TableBody, TableContainer, Typography } from '@mui/material';

const ALERT_STATS = [
  { label: 'Open', value: 1 },
  { label: 'Acknowledge', value: 1 },
  { label: 'Resolved', value: 1 },
  { label: 'Total', value: 3 },
];

export default function Alerts() {
  return (
    <Grid container direction="column" spacing={2} sx={{ flex: 1, minHeight: 0 }}>
      {/* 标题（自然高度） */}
      <Grid xs={12}>
        <Typography variant="h6">Alerts</Typography>
      </Grid>

      {/* 统计卡片（自然高度） */}
      <Grid xs={12}>
        <Grid container spacing={2}>
          {ALERT_STATS.map((i) => (
            <Grid key={i.label} size={{ xs: 12, sm: 4, md: 3 }}>
              <Paper sx={{ p: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  {i.label}
                </Typography>
                <Typography variant="h5" fontWeight={700}>
                  {i.value}
                </Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Grid>

      {/* 底部主区：占满剩余高度 */}
      <Grid xs={12} sx={{ flex: 1, minHeight: 0 }}>
        <Grid container spacing={1} sx={{ height: '100%', minHeight: 0 }}>
          {/* 左侧列表（宽度 3/12） */}
          <Grid size={{ xs: 12, md: 3 }}>
            <Paper sx={{ p: 2, height: '100%' }}>List</Paper>
          </Grid>

          {/* 右侧内容（宽度 9/12），内部用 CSS Grid 做 3:1 垂直分割 */}
          <Grid size={{ xs: 12, md: 9 }} sx={{ minHeight: 0 }}>
            <Box
              sx={{
                display: 'grid',
                gridTemplateRows: '3fr 1fr', // 上下 3:1
                gap: 8 / 8, // 与 MUI spacing 对齐（1 = 8px）
                height: '100%',
                minHeight: 0,
              }}
            >
              <Paper sx={{ p: 1 }}>ScreenShot</Paper>

              <TableContainer component={Paper} sx={{ minHeight: 0, overflow: 'auto' }}>
                <Table size="small">
                  <TableBody>{/* rows... */}</TableBody>
                </Table>
              </TableContainer>
            </Box>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
}
